mapper.select:
    	SELECT B.LOTID AS LOT_ID,
    		   B.SEQ,
    		   REPLACE(B.REMOTEFILENAME, '\', '/') AS REMOTE_FILE_NAME,
    		   B.FILENAME AS FILE_NAME
		FROM STD_CALENDAR A,
		     YMS_FILEINDEX_10Y B,
		     POP_LOT C
		WHERE 1=1
		  AND A.FACTORYDATE BETWEEN #{startTime} AND #{endTime}
		  AND B.TKOUTTIME BETWEEN TO_DATE(A.STARTTIME,'YYYYMMDDHH24MISS') AND TO_DATE(A.ENDTIME,'YYYYMMDDHH24MISS')


		  		#{stepSeq}




				#{productionType}




			  	#{div}




			  	#{productSpecGroup}




			  	#{productSpecName}




			  	#{program}


		  AND B.LOTID = C.LOTNAME


			  	#{lotId}




			  	#{target}




			  	#{chipSpec}




			  	#{frameName}


		ORDER BY B.LOTID, B.SEQ

    	SELECT B.STEPSEQ,
	         B.LOTID,
	         TO_CHAR(B.TKINTIME, 'YYYYMMDDHH24MISS') AS TKINTIME,
         	 TO_CHAR(B.TKOUTTIME, 'YYYYMMDDHH24MISS') AS TKOUTTIME,
	         B.EQPID || '(' || M.DESCRIPTION || ')' AS EQPID,
	         B.PRODUCTSPECNAME AS PARTID,
	         B.EMPNO,
	         B.PROGRAM,
	         B.LOTTYPE,
	         C.CHIPSPEC,
	         C.FRAMENAME,
	         C.TARGET,
	         C.MOLDMACHINENAME,
	         B.FMKEY,
	         REPLACE(B.REMOTEFILENAME, '\', '/') AS REMOTEFILENAME,
	         B.FILENAME
	    FROM STD_CALENDAR A,
	         YMS_FILEINDEX_10Y B,
	         POP_LOT C,
	         POP_MACHINESPEC M
		WHERE 1=1
		  AND A.FACTORYDATE BETWEEN #{startTime} AND #{endTime}
		  AND B.TKOUTTIME BETWEEN TO_DATE(A.STARTTIME,'YYYYMMDDHH24MISS') AND TO_DATE(A.ENDTIME,'YYYYMMDDHH24MISS')
		  AND B.LOTID = C.LOTNAME
		  AND B.EQPID = M.MACHINENAME


		  		#{stepSeq}




				#{productionType}




			  	#{div}




			  	#{productSpecGroup}




			  	#{productSpecName}




			  	#{program}




			  	#{lotId}




			  	#{target}




			  	#{chipSpec}




			  	#{frameName}


		ORDER BY B.LOTID, B.SEQ

    	SELECT DISTINCT B.ITEMNAME
		  FROM STD_CALENDAR S, 
		       YMS_FILEINDEX_10Y A, 
		       YMS_MEASUREITEMNAME B,
		       POP_LOT L
		 WHERE S.FACTORYNAME = #{site}
		    AND S.FACTORYDATE BETWEEN #{startDate} AND #{endDate}
		    AND A.TKOUTTIME BETWEEN TO_DATE(S.STARTTIME,'YYYYMMDDHH24MISS') AND TO_DATE(S.ENDTIME,'YYYYMMDDHH24MISS')
		    AND A.LOTID = L.LOTNAME
		    AND A.FMKEY = B.FMKEY



					#{stepSeq}




					#{stepType}




					#{productionType}




					#{site}




					#{div}




					#{productSpecGroup}




					#{productSpecName}




					#{lotId}



				AND A.LOTID LIKE #{lotText} || '%'



					#{machine}




					#{program}




					#{target}




					#{chipSpec}




					#{intensity}




					#{frameName}




					#{pl}


		    ORDER BY ITEMNAME ASC

    	SELECT MATNR AS PARTID, ZTEPG AS PROGRAM, CIE, X1, Y1, X2, Y2, X3, Y3, X4, Y4
		FROM POP_ERP_SEPERATOR_CIE
		WHERE WERKS = #{factoryName}
		  AND ZTEPG = #{program}
		  AND MATNR = #{productSpecName}
		ORDER BY CIE ASC

    	SELECT MAX(CASE WHEN DEFAULTFLAG = 'X' THEN ENUMVALUE END) CIEX,
		       MAX(CASE WHEN DEFAULTFLAG = 'Y' THEN ENUMVALUE END) CIEY
		FROM POP_ENUMDEFVALUE
		WHERE ENUMNAME = 'CIE-XY-NAME'
		  AND 

			  #{cieX}


			  #{cieY}

          )

SELECT KEY, FACTORYNAME, DIV, STARTDT, ENDDT, STEPSEQ, LOTTYPE, PRODUCTSPECGROUP, PRODUCTSPECNAME, PROGRAM, TARGET, CHIPSPEC, FRAMENAME, INTENSITY, PL, LOTID, USERID, FILEPATH, TO_CHAR (CREATETIME, 'YYYY-MM-DD HH24:MI:SS') AS CREATETIME, TO_CHAR(BYTES, 'FM999,999,999,999') AS BYTES, COMMENTS FROM YMS_ANAL_HISTORY
AND KEY = #{key}
AND USERID = #{userId}

mapper.update:
UPDATE YMS_ANAL_HISTORY
SET COMMENTS = #{comments}
WHERE KEY = #{key}

mapper.delete:
DELETE YMS_ANAL_HISTORY_SHARE
WHERE KEY = #{key}

	AND USERID = #{userId}

mapper.update:
UPDATE YMS_ANAL_HISTORY
SET SHAREYN =  #{shared}
WHERE KEY = #{key}

UPDATE YMS_ANAL_HISTORY
SET DELYN = #{delYn}
WHERE KEY = #{key}
