mapper.select:
SELECT 'False' AS "selected", A.FMKEY AS "fmKey", A.LOTID as "lotId", P.DIV as "div", P.PRODUCTSPECGROUP as "productSpecGroup", A.PRODUCTSPECNAME as "partId", A.PROGRAM as "program", A.FILENAME as "fileName", A.SEQ as "slotNo", A.STEPSEQ as "stepSeq", A.STEPTYPE as "stepType", TO_CHAR(A.TKINTIME, 'YYYY-MM-DD HH24:MI:SS') AS "tkInTime", TO_CHAR(A.TKOUTTIME, 'YYYY-MM-DD HH24:MI:SS') AS "tkOutTime", M.DESCRIPTION AS "machineName", A.EQPID as "eqpId", B.TARGET as "target", B.CHIPSPEC as "chipSpec", B.INTENSITY as "intensity", B.PL as "pl", B.FRAMENAME as "frameName", B.MOLDMACHINENAME as "moldMachineName" FROM STD_CALENDAR S, YMS_FILEINDEX_10Y A, POP_LOT B, V_PRODUCTSPEC P, POP_MACHINESPEC M WHERE S.FACTORYNAME = #{site} AND S.FACTORYDATE BETWEEN #{startDate} AND #{endDate} AND A.TKOUTTIME BETWEEN TO_DATE (S.STARTTIME, 'YYYYMMDDHH24MISS') AND TO_DATE (S.ENDTIME, 'YYYYMMDDHH24MISS') AND A.LOTID = B.LOTNAME(+) AND A.PRODUCTSPECNAME = P.PRODUCTSPECNAME(+) AND A.EQPID = M.MACHINENAME(+)
#{stepSeq}
#{div}
#{productGroup}
#{productSpecName}
#{program}
#{lotId}
AND A.LOTID LIKE #{lotLike} || '%'
#{target}
#{chipSpec}
#{frameName}
#{pl}
#{machine}
ORDER BY LOTID, TKINTIME

SELECT FMKEY ,SUBFMKEY ,LOTID ,SEQ ,FILENAME ,LOTNAME ,WAFERID ,STEPSEQ ,STEPTYPE ,TKINTIME ,TKOUTTIME ,EQPID ,DIV ,PRODUCTSPECGROUP ,PRODUCTSPECNAME ,PROGRAM ,REPLACE(SUBSTR(REMOTEFILENAME, 1, INSTR(REMOTEFILENAME, '\', -1)), '\', '/') AS PATH ,REPLACE(REMOTEFILENAME, '\', '/') AS REMOTEFILENAME ,STATUS ,ERRMSG ,BYTES FROM YMS_FILEINDEX_10Y
AND FMKEY = #{fmKey}
#{key}
ORDER BY SUBFMKEY ASC
